const timeout_warning = function(data, next) {
    // if (document.getElementById("timeout-warning" + trial_number) != null) {
    //     document.getElementById('timeout-warning' + trial_number).style.visibility = 'hidden';
    // }
    document.getElementById('timeout-warning').style.visibility = 'hidden';
    clearTimeout(timeout_id_warning);
    timeout_id_warning = setTimeout(() => {
        document.getElementById('timeout-warning').style.visibility = 'visible';
    }, 1500);
    clearTimeout(timeout_id_visibility);
    timeout_id_visibility = setTimeout(() => {
        document.getElementById('stimulus').style.visibility = 'hidden';
    }, 500);
    next();
};

